import { Text, Center, Flex, Box } from "@chakra-ui/react";
import cardDetails from "./data";

export default function Card() {
	return (
		<>
			{cardDetails.map(({ title, department, price, productDescription }, index) => (
				<Center key={index} maxW="sm" borderWidth="1px" borderColor="red.400" borderRadius="lg" mx="5">
					<Flex direction="column" p="3" bg="gray.200" borderRadius="lg" mx="2">
						<Text mb="2" color="blue.700">
							{title}
						</Text>
						<Text mb="2" color="blue.700">
							{department}
						</Text>
						<Text mb="2" color="blue.700">
							{price}
						</Text>
						<Box bg="pink.400" color="black">
							{productDescription}
						</Box>
					</Flex>
				</Center>
			))}
		</>
	);
}
