import Head from "next/head";
import { useState } from "react";
import { ChakraProvider, Box, Center, Button, Container, Flex } from "@chakra-ui/react";
import Link from "next/link";
import Layout from "../components/Layout";

function App() {
	const [count, setCount] = useState(0);

	const handleIncrement = () => {
		setCount(() => count + 1);
	};
	const handleDecrement = () => {
		setCount(() => count - 1);
	};
	return (
		<ChakraProvider>
			<Layout>
				<Head>
					<title>Next Practice</title>
				</Head>

				<Center p={4} direction="column" bg="gray.500">
					<Box bg="black" w="100%" p={4} color="red.500">
						{count}
						<Button outline="none" m="4" p="5" onClick={handleIncrement}>
							+
						</Button>
						<Button p="5" onClick={handleDecrement}>
							-
						</Button>
					</Box>
				</Center>
				<Container bg="blue.600" color="red.300">
					<Link href="/about">
						<a>About </a>
					</Link>
					<Link href="/home">
						<a>Home</a>
					</Link>
					<Link href="/contact/[id]">
						<a>Contact</a>
					</Link>
				</Container>
			</Layout>
		</ChakraProvider>
	);
}
export default App;
