import Head from "next/head";
import Navbar from "../components/Navbar";
import { useState } from "react";
import { ChakraProvider, Box, Center, Button, Container, Flex } from "@chakra-ui/react";
import Link from "next/link";
import Layout from "../components/Layout";
import { Alert, AlertIcon, AlertTitle, AlertDescription, CloseButton } from "@chakra-ui/react";

function App() {
	const [count, setCount] = useState(0);

	const handleIncrement = () => {
		setCount(() => count + 1);
	};
	const handleDecrement = () => {
		setCount(() => count - 1);
	};

	const [close, setClose] = useState(false);
	return (
		<ChakraProvider>
			<Layout>
				<Head>
					<title>Next Practice</title>
				</Head>
				{close ? (
					""
				) : (
					<Alert status="error">
						<AlertIcon />
						<AlertTitle mr={2}>Your browser is outdated!</AlertTitle>
						<AlertDescription>Your Chakra experience may be degraded.</AlertDescription>
						<CloseButton onClick={() => setClose(true)} position="absolute" right="8px" top="8px" />
					</Alert>
				)}
				<Navbar />
				<Center p={4} direction="column" bg="black" w="100%" p={4} color="red.500">
					{count}
					<Button shadow="xl" m="4" p="5" onClick={handleIncrement}>
						+
					</Button>
					<Button p="5" onClick={handleDecrement}>
						-
					</Button>
				</Center>
			</Layout>
		</ChakraProvider>
	);
}
export default App;
