import { Text, Center, Flex, Box, Container } from "@chakra-ui/react";
import cardDetails from "./data";

export default function Card() {
	return (
		<>
			<Center>
				<Text as="h1">Check out our products</Text>
			</Center>
			<Container>
				{cardDetails.map(({ title, dept, price, description }, index) => (
					<Center
						key={index}
						maxW="100%"
						borderWidth="1px"
						mb="3"
						borderColor="gray.400"
						borderRadius="lg"
						mx="5"
					>
						<Flex direction="column" p="3" borderRadius="lg" mx="2">
							<Text mb="2" color="blue.700">
								{title}
							</Text>
							<Box bg="green.300" p="2" borderRadius="lg" mb="2" color="blue.700">
								{dept}
							</Box>
							<Text mb="2" color="blue.700">
								$ {price}
							</Text>
							<Box color="black">{description}</Box>
						</Flex>
					</Center>
				))}
			</Container>
		</>
	);
}
