import { Text, Center, Flex, Box, Container, Button } from "@chakra-ui/react";
import cardDetails from "./data";

export default function Card() {
	return (
		<>
			<Center>
				<Text mb="3" as="h1">
					Check out our products
				</Text>
			</Center>
			<Container>
				{cardDetails.map(({ title, dept, price, description }, index) => (
					<Center
						key={index}
						maxW="100%"
						borderWidth="1px"
						mb="3"
						borderColor="gray.400"
						borderRadius="lg"
						mx="5"
					>
						<Flex
							direction={["column", "column", "column", "row"]}
							p={[3, 5, 7, 9]}
							borderRadius="lg"
							mx="2"
						>
							<Text mb="2" color="blue.700">
								{title}
							</Text>
							<Box bg="pink.200" p="2" borderRadius="lg" mb="2" color="blue.700">
								{dept}
							</Box>
							<Text mb="2" color="blue.700">
								$ {price}
							</Text>
							<Box color="black">{description}</Box>
							<Center>
								<Button
									bg="blue.300"
									color="gray.50"
									borderWidth="1px"
									borderRadius="lg"
									p="1.5"
									m="2"
									_hover={{
										bg: "blue.600",
										color: "gray.100"
									}}
								>
									Add to Cart
								</Button>
							</Center>
						</Flex>
					</Center>
				))}
			</Container>
		</>
	);
}
